$(document).ready(function(){var c=$("#imageList .controls :first-child"),d=$("#imageList .controls :last-child");c.click(function(){console.log("up");var a=$(this).parents("li");if(a.is(":first-of-type"))console.log("first");else{var b=a.prev();a.animate({bottom:b.outerHeight()+8+"px"},500,function(){$(this).after(b.detach()).prop("style","")});b.animate({top:a.outerHeight()+8+"px"},500,function(){$(this).prop("style","")})}});d.click(function(){console.log("down");var a=$(this).parents("li");if(a.is(":last-of-type"))console.log("last");
else{var b=a.next();a.animate({top:b.outerHeight()+8+"px"},500,function(){$(this).before(b.detach()).prop("style","")});b.animate({bottom:a.outerHeight()+8+"px"},500,function(){$(this).prop("style","")})}})});
